        return new Source("{% extends 'base.html.twig' %}

{% block title %}<title>My Profile</title>{% endblock %}

{% block body %}
    <div class=\"contact-box\">
        <div class=\"container\">
            <div class=\"row\">
                <div class=\"col-lg-12\">
                    <div class=\"heading-title text-center\">
                        <h2>My Profile</h2>
                    </div>
                </div>
            </div>
            <div class=\"row\">
                <div class=\"col-lg-12\">
                    <div class=\"row\">
                        {% for message in app.flashes('message') %}
                            <div class=\"col-sx4\">
                                <div class=\"p-3 my-2 rounded-2 success\">{{ message }}</div>
                            </div>
                        {% endfor %}
                        <div class=\"col-md-12\">
                            <div class=\"form-group\">
                                <p>Firstname : {{ app.user.firstname }}</p>
                                <p>Lastname : {{ app.user.lastname }}</p>
                                <p>Email : {{ app.user.email }}</p>
                                <p>Adresse : {{ app.user.adresse }}</p>
                                <p>Telephone : {{ app.user.telephone }}</p>
                                <a href=\"{{ path('update_user_profile') }}\">Update Profile</a>
                                <a href=\"{{ path('change_user_password') }}\">Update Password</a>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    </div>
{% endblock %}", "users/index.html.twig", "C:\\Users\\elbarhoumi\\OneDrive\\Desktop\\PROJET PI\\middlefeast\\templates\\users\\index.html.twig");
