    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}<title>Reset your password</title>{% endblock %}

{% block body %}
<div class=\"contact-box\">
    <div class=\"container\">
        <div class=\"row\">
            <div class=\"col-lg-12\">
                <div class=\"heading-title text-center\">
                    <h2>Reset Password</h2>
                </div>
            </div>
        </div>
        <div class=\"row\">
            <div class=\"col-lg-12\">
                {% for flashError in app.flashes('reset_password_error') %}
                    <div class=\"alert alert-danger\" role=\"alert\">{{ flashError }}</div>
                {% endfor %}
                {{ form_start(requestForm) }}
                <div class=\"col-md-12\">
                    <div class=\"form-group\">
                        {{ form_row(requestForm.email) }}
                        <div>
                            <small>
                                Enter your email address and we will send you a
                                link to reset your password.
                            </small>
                        </div>
                    </div>
                </div>
                <div class=\"col-md-12\">
                    <div class=\"submit-button text-center\">
                        <button class=\"btn btn-common\">Send password reset email</button>
                    </div>
                </div>
                {{ form_end(requestForm) }}
            </div>
        </div>
    </div>
</div>
{% endblock %}
", "reset_password/request.html.twig", "C:\\Users\\elbarhoumi\\OneDrive\\Desktop\\PROJET PI\\middlefeast\\templates\\reset_password\\request.html.twig");
